import React, { useState, useEffect } from 'react';
import Calendar from './Calendar';
import EventList from './EventList';
import './App.css';
import AddEvent from './AddEvent';

function App() {
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [isAddEventModalOpen, setAddEventModalOpen] = useState(false);

  useEffect(() => {
    fetch('http://localhost:4000/api/events')
      .then((response) => response.json())
      .then((data) => setEvents(data));
  }, []);

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const handleOpenAddEventModal = () => {
    setAddEventModalOpen(true);
  };

  const handleCloseAddEventModal = () => {
    setAddEventModalOpen(false);
  };

  const handleEventAdded = () => {
    fetch('http://localhost:4000/api/events')
      .then((response) => response.json())
      .then((data) => setEvents(data));

    handleCloseAddEventModal();
  };

  return (
    <div className="App">
      <h1>Calendar App</h1>
      <button onClick={handleOpenAddEventModal}>Add Event</button>
      <Calendar events={events} selectedDate={selectedDate} onDateChange={handleDateChange} />
      <EventList events={events} />
      {isAddEventModalOpen && (
        <AddEvent onClose={handleCloseAddEventModal} onEventAdded={handleEventAdded} />
      )}
    </div>
  );
}

export default App;
