import React, { useState } from 'react';
import './EventForm.css'; 

function EventForm({ onAddEvent, date, onClose }) {
  const [eventData, setEventData] = useState({
    title: '',
    description: '',
    start: date ? date.toISOString().slice(0, 16) : '',
    end: date ? date.toISOString().slice(0, 16) : '',
    priority: 'low',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEventData({ ...eventData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddEvent(eventData);
  };

  return (
    <div className="event-form">
      <h2>Add Event</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            name="title"
            value={eventData.title}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="description">Description:</label>
          <textarea
            name="description"
            value={eventData.description}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="start">Start Date and Time:</label>
          <input
            type="datetime-local"
            name="start"
            value={eventData.start}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="end">End Date and Time:</label>
          <input
            type="datetime-local"
            name="end"
            value={eventData.end}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="priority">Priority:</label>
          <select name="priority" value={eventData.priority} onChange={handleChange}>
            <option value="low">Low</option>
            <option value="medium">Medium</option>
            <option value="high">High</option>
          </select>
        </div>
        <div className="button-group">
          <button type="submit" className="submit-button">Add Event</button>
          <button type="button" className="cancel-button" onClick={onClose}>Cancel</button>
        </div>
      </form>
    </div>
  );
}

export default EventForm;
