import React from 'react';
import Calendar from 'react-calendar';
import './App.css';

function CalendarComponent({ events, selectedDate, onDateChange }) {
  const eventDates = events.map((event) => new Date(event.date).toDateString());

  const tileContent = ({ date }) => {
    const dateString = date.toDateString();

    return (
      <div className="event-indicator">
        {eventDates.includes(dateString) && <div className="event-dot"></div>}
      </div>
    );
  };

  return (
    <div>
      <Calendar
        value={selectedDate}
        tileContent={tileContent}
        onClickDay={(value, event) => onDateChange(value)}
      />
    </div>
  );
}

export default CalendarComponent;
