import './styles.css';
import React, { useState } from 'react';
import EventList from './eventList';
import EventForm from './EventForm';
import DevCalendar from './calendar';
import Modal from 'react-modal';

Modal.setAppElement('#root');

function App() {
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [isAddingEvent, setIsAddingEvent] = useState(false);

  const addEvent = (eventData) => {
    fetch('http://localhost:4000/api/events', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(eventData),
    })
      .then((response) => response.json())
      .then((newEvent) => {
        setEvents([...events, newEvent]);
        setIsAddingEvent(false);
      })
      .catch((error) => {
        console.error('Error creating event:', error);
      });
  }

  const dateWithEventsStyle = {
    backgroundColor: '#007bff',
    color: '#fff',
    borderRadius: '50%',
  };

  const handleDayClick = (date) => {
    setSelectedDate(date);
    setIsAddingEvent(true);
  };

  return (
    <div className="container">
      <h1>My Calendar App</h1>
       <DevCalendar
        onDayClick={handleDayClick}
        events={events}
        tileContent={(props) => {
          const date = props.date;
          if (events.some((event) => event.start.includes(date.toISOString().slice(0, 10)))) {
            return <div style={dateWithEventsStyle}>{props.date.getDate()}</div>;
          }
          return props.date.getDate();
        }}
      />
      <EventList events={events} />
      {isAddingEvent && (
        <div className="modal">
          <EventForm
            onAddEvent={addEvent}
            date={selectedDate}
            onClose={() => setIsAddingEvent(false)}
          />
        </div>
      )}
    </div>
  );
}

export default App;
