import React from 'react';
import Calendar from 'react-calendar';
import './calendar.css'; 

function DevCalendar({ onDayClick, events }) {
  // Define a function to check if a date has events
  const hasEvents = (date) => {
    const formattedDate = date.toISOString().slice(0, 10);
    return events.some((event) => event.start.includes(formattedDate));
  };

  // Define a function to apply different CSS classes for dates with and without events
  const tileClassName = ({ date }) => {
    if (hasEvents(date)) {
      return 'date-with-events';
    }
    return 'date-without-events';
  };

  return (
    <div className="calendar-container">
      <h2>Calendar</h2>
      <Calendar onClickDay={onDayClick} tileClassName={tileClassName} />
    </div>
  );
}

export default DevCalendar;
