import React, { useState } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import moment from 'moment';
import EventDetailsModal from './EventDetailsModal';
import { fetchEvents, createEvent } from './api'; 

const localizer = momentLocalizer(moment);

const MyCalendar = ({ events, onAddEvent }) => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);

  const eventStyleGetter = (event) => {
    const style = {
      backgroundColor:
        event.priority === 'low'
          ? 'green'
          : event.priority === 'medium'
          ? 'orange'
          : 'red',
    };
    return {
      style,
    };
  };

  const handleSelectSlot = (slotInfo) => {
    setSelectedDate(slotInfo.start);
    setIsModalOpen(true);
  };

  const handleEventClick = (event, e) => {
    setSelectedEvent(event);
    setIsModalOpen(true);
  };

  const handleAddEvent = (newEvent) => {
    // Implement event creation logic, e.g., using your API
    createEvent(newEvent) // Assuming createEvent is a function in your API
      .then((createdEvent) => {
        // After creating the event, update the events array with the new event
        setEvents([...events, createdEvent]);
        setIsModalOpen(false); // Close the event details modal
      })
      .catch((error) => console.error('Error adding event:', error));
  };

  return (
    <div>
      <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        eventPropGetter={eventStyleGetter}
        style={{ height: 500 }}
        selectable
        onSelectEvent={handleEventClick}
      />
      <EventDetailsModal
        isOpen={isModalOpen}
        onRequestClose={() => {
          setIsModalOpen(false);
          setSelectedEvent(null);
        }}
        event={selectedEvent}
      />
    </div>
  );
};

export default MyCalendar;
