// src/AddEvent.js

import React, { useState } from 'react';
import './App.css'; // Import your CSS file

function AddEvent() {
  const [newEvent, setNewEvent] = useState({
    title: '',
    date: '',
    priority: 'low',
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewEvent({
      ...newEvent,
      [name]: value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Send the newEvent data to your API to create a new event
    // You will need to implement this API call.
  };

  return (
    <div>
      <h2 className="form-heading">Add Event</h2>
      <form className="event-form" onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          value={newEvent.title}
          onChange={handleInputChange}
          placeholder="Event title"
        />
        <input
          type="date"
          name="date"
          value={newEvent.date}
          onChange={handleInputChange}
        />
        <select name="priority" value={newEvent.priority} onChange={handleInputChange}>
          <option value="low">Low</option>
          <option value="medium">Medium</option>
          <option value="high">High</option>
        </select>
        <button type="submit">Add Event</button>
      </form>
    </div>
  );
}

export default AddEvent;
