// AddEventForm.js
import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import Modal from 'react-modal';
import './App.css'; // Import the CSS file

const AddEventForm = ({ selectedDate, onAddEvent, onClose }) => {
  const [newEvent, setNewEvent] = useState({
    title: '',
    description: '',
    start: selectedDate,
    end: selectedDate,
    priority: 'low',
  });

  const handleSubmit = () => {
    onAddEvent(newEvent);
    // Optionally close the modal after submitting
    onClose();
  };

  return (
    <Modal
      isOpen={true} // Make sure this is set to true
      onRequestClose={onClose}
      contentLabel="Add Event"
      className="event-modal" // Add this class name
      overlayClassName="event-modal-overlay" // Add this overlay class name
    >
      <div>
        <input
          type="text"
          placeholder="Event Title"
          value={newEvent.title}
          onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="Event Description"
          value={newEvent.description}
          onChange={(e) => setNewEvent({ ...newEvent, description: e.target.value })}
        />
        <DatePicker
          selected={newEvent.start}
          onChange={(date) => setNewEvent({ ...newEvent, start: date })}
          showTimeSelect
          dateFormat="Pp"
          placeholderText="Start Date and Time"
        />
        <DatePicker
          selected={newEvent.end}
          onChange={(date) => setNewEvent({ ...newEvent, end: date })}
          showTimeSelect
          dateFormat="Pp"
          placeholderText="End Date and Time"
        />
        <select
          value={newEvent.priority}
          onChange={(e) => setNewEvent({ ...newEvent, priority: e.target.value })}
        >
          <option value="low">Low</option>
          <option value="medium">Medium</option>
          <option value="high">High</option>
        </select>
        <button onClick={handleSubmit}>Save Event</button>
      </div>
    </Modal>
  );
};

export default AddEventForm;
