import React, { useState } from 'react';
import Calendar from 'react-calendar';

function MyCalendar({ onDayClick }) {
  const [date, setDate] = useState(new Date());

  const handleDateChange = (newDate) => {
    setDate(newDate);
  };

  const handleDayClick = () => {
    onDayClick(date);
  };

  return (
    <div>
      <h2>Calendar</h2>
      <Calendar value={date} onChange={handleDateChange} onClickDay={handleDayClick} />
    </div>
  );
}
