import React from 'react';
import Calendar from 'react-calendar';

function CalendarComponent({ events, selectedDate, onDateChange }) {
  const eventDates = events.map((event) => new Date(event.date));

  const tileContent = ({ date, view }) => {
    if (view === 'month') {
      const hasEvent = eventDates.some((eventDate) =>
        isSameDay(new Date(date), new Date(eventDate))
      );

      return hasEvent ? <div className="event-indicator"></div> : null;
    }
  };

  const isSameDay = (date1, date2) => {
    return (
      date1.getFullYear() === date2.getFullYear() &&
      date1.getMonth() === date2.getMonth() &&
      date1.getDate() === date2.getDate()
    );
  };

  return (
    <div>
      <Calendar
        value={selectedDate}
        tileContent={tileContent}
        onClickDay={(value, event) => onDateChange(value)}
      />
    </div>
  );
}

export default CalendarComponent;
