import React, { useState, useEffect } from 'react';
import MyCalendar from './MyCalendar';
import { fetchEvents, createEvent } from './api';
import moment from 'moment';
import EventDetailsModal from './EventDetailsModal';

const App = () => {
  const [events, setEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [isEventDetailsModalOpen, setEventDetailsModalOpen] = useState(false);

  useEffect(() => {
    fetchEvents()
      .then((data) => setEvents(data))
      .catch((error) => console.error('Error fetching events:', error));
  }, []);

  const handleAddEvent = (date, newEvent) => {
    const eventToAdd = {
      title: newEvent.title,
      priority: newEvent.priority,
      start: date,
      end: moment(date).add(1, 'hour').toDate(),
    };

    createEvent(eventToAdd)
      .then((data) => {
        setEvents([...events, data]);
      })
      .catch((error) => console.error('Error adding event:', error));
  };

  const handleEventClick = (event) => {
    setSelectedEvent(event);
    setEventDetailsModalOpen(true);
  };

  return (
    <div>
      <MyCalendar events={events} onAddEvent={handleAddEvent} onEventClick={handleEventClick} />
      {isEventDetailsModalOpen && selectedEvent && (
        <EventDetailsModal isOpen={true} onRequestClose={() => setEventDetailsModalOpen(false)} event={selectedEvent} />
      )}
    </div>
  );
};

export default App;
