import React, { useState, useEffect } from 'react';
import MyCalendar from './MyCalendar';
import { fetchEvents, createEvent } from './api';
import moment from 'moment';

const App = () => {
  const [events, setEvents] = useState([]);
  const [isEventDetailsModalOpen, setIsEventDetailsModalOpen] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);

  useEffect(() => {
    fetchEvents()
      .then((data) => setEvents(data))
      .catch((error) => console.error('Error fetching events:', error));
  }, []);

  return (
    <div>
      <button onClick={() => setIsEventDetailsModalOpen(true)}>Add Event</button>
      <MyCalendar
        events={events}
        onAddEvent={(date, newEvent) => {
          const eventToAdd = {
            title: newEvent.title,
            description: newEvent.description,
            start: date,
            end: newEvent.end,
            priority: newEvent.priority,
          };

          createEvent(eventToAdd)
            .then((data) => {
              setEvents([...events, data]);
              setIsEventDetailsModalOpen(false);
            })
            .catch((error) => console.error('Error adding event:', error));
        }}
      />

      {isEventDetailsModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setIsEventDetailsModalOpen(false)}>
              &times;
            </span>
            <h2>Event Details</h2>
            <p><strong>Title:</strong> {selectedEvent.title}</p>
            <p><strong>Description:</strong> {selectedEvent.description}</p>
            <p><strong>Start:</strong> {moment(selectedEvent.start).format('LLL')}</p>
            <p><strong>End:</strong> {moment(selectedEvent.end).format('LLL')}</p>
            <p><strong>Priority:</strong> {selectedEvent.priority}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
