import React, { useState } from 'react';
import Modal from 'react-modal';
import styled from 'styled-components';
import axios from 'axios';

const ModalContainer = styled.div`
  background: #fff;
  border: 1px solid #ccc;
  padding: 20px;
  max-width: 100%;
  width: 400px; /* Set your desired maximum width */
`;

const ModalHeader = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 1rem;
`;

const FormField = styled.div`
  margin-bottom: 1rem;
`;

const Input = styled.input`
  width: 100%;
  padding: 0.5rem;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Select = styled.select`
  width: 100%;
  padding: 0.5rem;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Button = styled.button`
  background: #007BFF;
  color: #fff;
  padding: 0.5rem 1rem;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 1rem;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 10px;
  right: 10px;
  background: transparent;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: #888;
`;

const EventDetails = ({ event, onUpdate, onDelete, onRequestClose }) => {

  return (
    <Modal
      isOpen={!!event}
      onRequestClose={onRequestClose}
      contentLabel="Event Details"
      style={{
        overlay: {
          zIndex: 9999,
        },
        content: {
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          padding: '0',
          border: 'none',
        },
      }}
    >
      <CloseButton onClick={onRequestClose}>X</CloseButton>
      <ModalContainer>
        <ModalHeader>Event Details</ModalHeader>
        <FormField>
          <Input
            type="text"
            placeholder="Event Title"
            value={event.title}
            onChange={(e) => onUpdate({ ...event, title: e.target.value })}
          />
        </FormField>
        <FormField>
          <Input
            type="text"
            placeholder="Event Description"
            value={event.description}
            onChange={(e) => onUpdate({ ...event, description: e.target.value })}
          />
        </FormField>
        <FormField>
          {/* Display and edit other event properties, such as date, time, and priority */}
        </FormField>
        <FormField>
          <Button onClick={() => onDelete(event)}>Delete Event</Button>
        </FormField>
      </ModalContainer>
    </Modal>
  );
};

export default EventDetails;
