import React, { useState } from 'react';
import './App.css'; 

function AddEvent({ onClose, onEventAdded }) {
  const [newEvent, setNewEvent] = useState({
    title: '',
    description: '',
    date: '',
    priority: 'low',
  });

  const [isAddEventModalOpen, setAddEventModalOpen] = useState(true);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewEvent({
      ...newEvent,
      [name]: value,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('your_api_endpoint', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newEvent),
      });

      if (response.ok) {
        setNewEvent({
          title: '',
          description: '',
          date: '',
          priority: 'low',
        });

        if (onEventAdded) {
          onEventAdded();
        }

        if (onClose) {
          onClose();
        }
      } else {
        console.error('Failed to add event. Server response:', response);
      }
    } catch (error) {
      console.error('Error adding event:', error);
    }
  };

  return (
    <div className={`modal ${isAddEventModalOpen ? 'visible' : ''}`}>
      <div className="modal-content">
        <span className="close" onClick={onClose}>
          &times;
        </span>
        <h2>Add Event</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-container">
            <input
              type="text"
              name="title"
              value={newEvent.title}
              onChange={handleInputChange}
              placeholder="Event title"
            />
          </div>
          <div className="input-container">
            <input
              type="text"
              name="description"
              value={newEvent.description}
              onChange={handleInputChange}
              placeholder="Event description"
            />
          </div>
          <div className="input-container">
            <input
              type="date"
              name="date"
              value={newEvent.date}
              onChange={handleInputChange}
            />
          </div>
          <div className="input-container">
            <select name="priority" value={newEvent.priority} onChange={handleInputChange}>
              <option value="low">Low</option>
              <option value="medium">Medium</option>
              <option value="high">High</option>
            </select>
          </div>
          <button type="submit">Add Event</button>
        </form>
      </div>
    </div>
  );
}

export default AddEvent;
