import React from 'react';
import Modal from 'react-modal';
import styled from 'styled-components';

const ModalContainer = styled.div`
  background: #fff;
  border: 1px solid #ccc;
  padding: 20px;
  max-width: 100%;
  width: 400px;
`;

const ModalHeader = styled.h2`
  font-size: 1.5rem;
  margin-bottom: 1rem;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 10px;
  right: 10px;
  background: transparent;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: #888;
`;

const EventDetailsModal = ({ isOpen, onRequestClose, event }) => {
  if (!event) {
    return null;
  }

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Event Details"
      style={{
        overlay: {
          zIndex: 9999,
        },
        content: {
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          padding: '0',
          border: 'none',
        },
      }}
    >
      <CloseButton onClick={onRequestClose}>X</CloseButton>
      <ModalContainer>
        <ModalHeader>Event Details</ModalHeader>
        <p><strong>Title:</strong> {event.title}</p>
        <p><strong>Description:</strong> {event.description}</p>
        <p><strong>Start Date and Time:</strong> {event.start.toString()}</p>
        <p><strong>End Date and Time:</strong> {event.end.toString()}</p>
        <p><strong>Priority:</strong> {event.priority}</p>
      </ModalContainer>
    </Modal>
  );
};

export default EventDetailsModal;
