import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const AddEventForm = ({ selectedDate, selectedEvent, onAddEvent }) => {
  const [newEvent, setNewEvent] = useState({
    title: '',
    description: '',
    start: selectedDate,
    end: selectedDate,
    priority: 'low',
  });

  useEffect(() => {
    if (selectedEvent) {
      setNewEvent({
        title: selectedEvent.title || '',
        description: selectedEvent.description || '',
        start: selectedEvent.start || selectedDate,
        end: selectedEvent.end || selectedDate,
        priority: selectedEvent.priority || 'low',
      });
    }
  }, [selectedEvent, selectedDate]);

  const handleSubmit = () => {
    onAddEvent(newEvent);
  };

  return (
    <div className="form-container">
      <h2>{selectedEvent ? 'Modify Event' : 'Add Event'}</h2>
      <div>
        <label>Title:</label>
        <input
          type="text"
          placeholder="Event Title"
          value={newEvent.title}
          onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })}
        />
      </div>
      <div>
        <label>Description:</label>
        <input
          type="text"
          placeholder="Event Description"
          value={newEvent.description}
          onChange={(e) => setNewEvent({ ...newEvent, description: e.target.value })}
        />
      </div>
      <div>
        <label>Start Date and Time:</label>
        <DatePicker
          selected={newEvent.start}
          onChange={(date) => setNewEvent({ ...newEvent, start: date })}
          showTimeSelect
          dateFormat="Pp"
          placeholderText="Start Date and Time"
        />
      </div>
      <div>
        <label>End Date and Time:</label>
        <DatePicker
          selected={newEvent.end}
          onChange={(date) => setNewEvent({ ...newEvent, end: date })}
          showTimeSelect
          dateFormat="Pp"
          placeholderText="End Date and Time"
        />
      </div>
      <div>
        <label>Priority:</label>
        <select
          value={newEvent.priority}
          onChange={(e) => setNewEvent({ ...newEvent, priority: e.target.value })}
        >
          <option value="low">Low</option>
          <option value="medium">Medium</option>
          <option value="high">High</option>
        </select>
      </div>
      <div>
        <button onClick={handleSubmit}>{selectedEvent ? 'Modify Event' : 'Save Event'}</button>
      </div>
    </div>
  );
};

export default AddEventForm;
